import React, { useState } from "react";
import {
  Picker,
  TextInput,
  Text,
  TouchableOpacity,
  View,
  Alert
} from "react-native";

function ProductForm(props) {

  const tierID = props.tier_ID

  const [inputNameState, setInputName] = useState("");
  const [inputQuantityState, setInputQuantity] = useState("");


  const [pickerState, setPickerValue] = useState("low");

  async function handleSubmit() {
    console.log(inputNameState)
    console.log(inputQuantityState)

    if (inputNameState && pickerState && inputQuantityState) {
      const newProduct = {
        tier_id: tierID,
        product_name: inputNameState,
        product_quantity: inputQuantityState

      };

      fetch("http://localhost:4001/products/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json"
        },
        body: JSON.stringify(newProduct)
      })
        .then(res => res.json())
        .then(res => {

          if (res) {
            Alert.alert(res.products);
            props.navigation.goBack();
          }
        })
        .catch(console.log);
    }
  }

  return (
    <View>
      <TextInput
        autoFocus
        style={{
          borderWidth: 1,
          borderColor: "#5ce0e9",
          borderTopColor: "white",
          color: "black",
          fontSize: 50,
          marginTop: 10,
          paddingLeft: 10
        }}
        value={inputNameState}
        onChangeText={text => setInputName(text)}
      />
       <TextInput
        autoFocus
        style={{
          borderWidth: 1,
          borderColor: "#5ce0e9",
          borderTopColor: "white",
          color: "black",
          fontSize: 50,
          marginTop: 10,
          paddingLeft: 10
        }}
        value={inputQuantityState}
        onChangeText={text => setInputQuantity(text)}
      />
      <TouchableOpacity
        onPress={handleSubmit}
        style={{
          borderRadius: 10,
          marginLeft: "auto",
          marginRight: "auto",
          marginTop: 30,
          backgroundColor: "#25aae1",
          padding: 5,
          height: 40,
          width: 80
        }}
      >
        <Text style={{ color: "white", fontWeight: "800", padding: 5 }}>
          Submit
        </Text>
      </TouchableOpacity>
    </View>
  );
}

export default ProductForm;
