import React, { useState } from "react";
import {
  Picker,
  TextInput,
  Text,
  TouchableOpacity,
  View,
  Alert
} from "react-native";

function TierForm(props) {
  const [inputState, setInput] = useState("");
  const [pickerState, setPickerValue] = useState("low");

  async function handleSubmit() {
    if (inputState && pickerState) {
      const newTier = {
        tier_name: inputState,
      };
      console.log(newTier)
      fetch("http://localhost:4001/tiers/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json"
        },
        body: JSON.stringify(newTier)
      })
        .then(res => res.json())
        .then(res => {

          if (res) {
            Alert.alert(res.tiers);
            props.navigation.goBack();
          }
        })
        .catch(console.log);
    }
  }

  return (
    <View>
      <TextInput
        autoFocus
        style={{
          borderWidth: 1,
          borderColor: "#5ce0e9",
          borderTopColor: "white",
          color: "black",
          fontSize: 50,
          marginTop: 10,
          paddingLeft: 10
        }}
        value={inputState}
        onChangeText={text => setInput(text)}
      />
      <TouchableOpacity
        onPress={handleSubmit}
        style={{
          borderRadius: 10,
          marginLeft: "auto",
          marginRight: "auto",
          marginTop: 30,
          backgroundColor: "#25aae1",
          padding: 5,
          height: 40,
          width: 80
        }}
      >
        <Text style={{ color: "white", fontWeight: "800", padding: 5 }}>
          Submit
        </Text>
      </TouchableOpacity>
    </View>
  );
}

export default TierForm;
